            proxyDelegateCalldata(
                DiamondHelper._getFacetAddress(SolvencyFacetProd.isSolventPayable.selector),
                abi.encodeWithSelector(SolvencyFacetProd.isSolventPayable.selector)
            ),
            (bool)
        );
