alType":"bool","name":"isCL","type":"bool"},{"internalType":"uint32","name":"shortTwap","type":"uint32"},{"components":[{"internalType":"uint32","name":"duration","type":"uint32"},{"internalType":"uint256","name":"maxDeviation","type":"uint256"}],"internalType":"struct IBaseOracle.TWAPCheck[]","name":"twapChecks","type":"tuple[]"},{"internalType":"address","name":"baseAsset","type":"address"},{"internalType":"enum IBaseOracle.Protocol","name":"protocol","type":"uint8"}],"internalType":"struct IBaseOracle.PoolConfig[]","name":"pools","type":"tuple[]"}],"name":"configureToken","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"address","name":"token","type":"address"}],"name":"getFullTokenConfig","outputs":[{"components":[{"internalType":"bool","name":"isConfigured","type":"bool"},{"components":[{"internalType":"address","name":"poolAddress","type":"address"},{"internalType":"bool","name":"isCL","type":"bool"},{"internalType":"uint32","name":"shortTwap","type":"uint32"},{"components":[{"internalType":"uint32","name":"duration","type":"uint32"},{"internalType":"uint256","name":"maxDeviation","type":"uint256"}],"internalType":"struct IBaseOracle.TWAPCheck[]","name":"twapChecks","type":"tuple[]"},{"internalType":"address","name":"baseAsset","type":"address"},{"internalType":"enum IBaseOracle.Protocol","name":"protocol","type":"uint8"}],"internalType":"struct IBaseOracle.PoolConfig[]","name":"pools","type":"tuple[]"}],"internalType":"struct IBaseOracle.TokenConfig","name":"","type":"tuple"}],"stateMutability":"view","type":"function"},{"inputs":[{"components":[{"internalType":"address","name":"asset","type":"address"},{"internalType":"uint256","name":"amount","type":"uint256"},{"internalType":"bool","name":"useTwapChecks","type":"bool"},{"internalType":"address[]","name":"baseAssets","type":"address[]"},{"internalType":"uint256[]","name":"baseAssetPrices","type":"uint256[]"}],"internalType":"struct IBaseOracle.GetDollarValueParams",