.","params":{"pools":"Array of pool configurations for the token.","token":"The token address."}},"constructor":{"custom:oz-upgrades-unsafe-allow":"constructor"},"getFullTokenConfig(address)":{"params":{"token":"The token address."},"returns":{"_0":"The token configuration."}},"getTokenDollarPrice((address,uint256,bool,address[],uint256[]))":{"details":"Reverts if no valid quote prices are found or if no valid TWAP prices are available","params":{"params":"GetDollarValueParams struct containing:        - asset: The asset token address        - amount: The amount in token's native decimals (e.g., 1000000 for 1 USDC)        - useTwapChecks: Whether to perform TWAP deviation checks        - baseAssets: Array of base asset addresses        - baseAssetPrices: Array of USD prices for the base assets (1e18 scale)"},"returns":{"_0":"The USD price per one token in 1e18 scale (e.g., 1e18 represents $1.00)"}},"initialize(address)":{"params":{"_initialOwner":"The address of the initial owner."}},"owner()":{"details":"Returns the address of the current owner."},"removeToken(address)":{"details":"Only callable by the owner.","params":{"token":"The token address."}},"renounceOwnership()":{"details":"Leaves the contract without owner. It will not be possible to call `onlyOwner` functions. Can only be called by the current owner. NOTE: Renouncing ownership will leave the contract without an owner, thereby disabling any functionality that is only available to the owner."},"transferOwnership(address)":{"details":"Transfers ownership of the contract to a new account (`newOwner`). Can only be called by the current owner."}},"title":"BaseOracle","version":1},"userdoc":{"kind":"user","methods":{"configureToken(address,(address,bool,uint32,(uint32,uint256)[],address,uint8)[])":{"notice":"Configures a token with its associated pools."},"getFullTokenConfig(address)":{"notice":"Retrieves the full configuration for a given token."},"getTokenDollarPrice((address,uint256,bool,address[],uint256[]))":{"notice":"Calculates the USD dollar price per one token (1e18 scale) of an asset based on its configured pools. The final price is determined by: 1. Finding maximum price among all pool quotes (both AMM and CL) 2. Finding minimum price among all CL pool TWAPs 3. Taking the minimum between (1) and (2)"},"initialize(address)":{"notice":"Initializes the contract and sets the initial owner."},"removeToken(address)":{"notice":"Removes a token and its associated pools from the configuration."}},"version":1}},"settings":{"compilationTarget":{"contracts/oracle/BaseOracle.sol":"BaseOracle"},"evmVersion":"london","libraries":{},"metadata":{"bytecodeHash":"ipfs","useLiteralContent":true},"optimizer":{"enabled":true,"runs":10},"remappings":[]},"sources":{"@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol":{"content":"// SPDX-License-Identifier: MIT
